PJT_NM, CUST_CD, CUST_NM, CUST_GBN, PLAN_DATE, BUSI_DATE, CUST_TEL, BUSI_YY, BUSI_SEQ, CRT_DATE, CUST_CHARGE_NM, REGION, REGION2, ZONE1, ZONE2, SCOPE1, SCOPE2, UP_SU, DOWN_SU, H_CNT, AREA_P, AREA_M, YAREA_P, YAREA_M, ADDR, COVERAGE, CAPACITY, RMK_1, BUILD_TYPE, BIZ_TYPE, BIZ_GBN, FORECAST_AMT, MARKET_CLASS_GBN, RMK_2, RMK_3, RMK_4, RMK_5, BUILD_GB
FROM T_PS3000 WHERE PJT_CD LIKE #{PJT_CD}
